import { createStore } from "vuex";
import axios from "axios";

const store = createStore({
  state() {
    return {
      users: [],
      editNote: null,
      editMode: {}
    };
  },
  mutations: {
    deleteUser(state, index) {
      state.users.splice(index, 1);
    },
    addUser(state, payload) {
      state.users.push(payload);
      console.log("User added:", payload);
    },
    loadUsers(state, payload) {
      state.users = payload;
      console.log("Users loaded:", payload);
    },
    setEditNote(state, noteId) {
      state.editNote = noteId;
      console.log("Note editing started:", noteId);
    },
  },
  actions: {
    addNewUser({ commit }, payload) {
      console.log("Adding user:", payload);
      commit("addUser", payload);
    },
    async loadUsersFromList({ commit }) {
      try {
        const response = await axios.get("../list");
        const users = response.data.people;
        commit("loadUsers", users);
      } catch (error) {
        console.error("Failed to load users:", error);
      }
    },
    editNote({ commit }, noteId) {
      commit("setEditNote", noteId);
    },
    deleteNote({ commit, state }, item) {
      if (state.peopleList && state.peopleList.length > 0) {
        const index = state.peopleList.findIndex((person) => person.id === item.id);
        if (index !== -1) {
          commit("deletePerson", index);
          return; // Возвращаемся после удаления существующей записи
        }
      }
      
      if (state.users && state.users.length > 0) {
        const newUserIndex = state.users.findIndex((user) => user.id === item.id);
        if (newUserIndex !== -1) {
          commit("deleteUser", newUserIndex);
        }
      }
    },
  },    
  getters: {
    getUsers: state => state.users
  }
});

export default store;
