import { createStore } from "vuex";
import axios from "axios";

const store = createStore({
  state: {
    users: [],
    editNote: null,
    editMode: {}
  },
  mutations: {
    addUser(state, payload) {
      state.users.push(payload);
      console.log("User added:", payload);
    },
    loadUsers(state, payload) {
      state.users = payload;
      console.log("Users loaded:", payload);
    }, 
    setEditNote(state, noteId) {
      state.editNote = noteId;
      console.log("Note editing started:", noteId);
    },
    SET_EDIT_MODE(state, { id, editMode }) {
      state.editMode[id] = editMode;
    },
    SET_USER_NAME(state, { id, name }) {
      state.users = state.users.map(user => {
        if (user.id === id) {
          return { ...user, name };
        }
        return user;
      });
    }
  },
  actions: {
    addNewUser({ commit }, payload) {
      console.log("Adding user:", payload);
      commit("addUser", payload);
    },
    loadUsersFromList({ commit }) {
      axios.get("../list.json")
        .then(response => {
          const users = response.data.people;
          commit("loadUsers", users);
        })
        .catch(error => {
          console.error("Failed to load users:", error);
        });
    },
    editNote({ commit }, noteId) {
      commit("setEditNote", noteId);
    },
    toggleEditMode({ commit, state }, id) {
      const editMode = !state.editMode[id];
      commit('SET_EDIT_MODE', { id, editMode });
    },
    saveUserName({ commit }, { id, name }) {
      commit('SET_USER_NAME', { id, name });
    },
  },
  getters: {
    getUsers: state => state.users
  }
});

export default store;
