// store.js
import { createStore } from "vuex";

const store = createStore({
  state: {
    users: [],
  },
  mutations: {
    Add_user(state, payload) {
      state.users.push(payload) // Добавление пользователя 
      console.log('6. action came. Finish!')
    },
  },
  actions: {
    addNewUser({commit}, payload) {
      console.log('5. store recieved')
      commit('Add_user', payload) // Вызов мутации для добавления пользователя
    },
    getters: {
      users: state => state.users
    },
  }  
});

export default store;
